<script lang="ts">
import { Component, Prop, Vue } from 'vue-property-decorator';
import Theme from '@soramitsu/soramitsu-js-ui/lib/types/Theme';

@Component
export default class PolkaswapLogo extends Vue {
  @Prop({ default: Theme.LIGHT, type: String }) readonly theme!: Theme;
  @Prop({ default: '', type: String }) readonly color!: string;

  get textColor(): string {
    if (this.color) return this.color;
    if (this.theme === Theme.DARK) return 'var(--s-color-base-content-primary)';
    return '#0D0248';
  }

  get logoColor(): string {
    return this.color || '#ED145B';
  }
}
</script>
